package com.class3;

public class shorthandOperation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num=1000;
		num=num+1000;
		
		System.out.println(num);
		
		int num4=50;
		num4+=20;
		
		System.out.println(num4);
		num4-=30;
		
		System.out.println(num4);
		
		num4/=10;
		System.out.println(num4);
		
		num4%=2;
		System.out.println(num4);
		
				

	}

}
