package com.class9;

public class assigniment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 1; i <= 5; i++) {
			for (int a = 1; a <= i; i++) {

				System.out.print("-");

				for (int j =1; j<=i; j++) {
					System.out.print("*");
					
				}
				

			}System.out.println();

		}

	}

}
